const cursor=document.querySelector(".cursor"),cursor2=document.querySelector(".cursor2");document.addEventListener("mousemove",(e=>{cursor.setAttribute("style","top: "+(e.pageY-8)+"px; left: "+(e.pageX-8)+"px;"),cursor2.setAttribute("style","top: "+(e.pageY-12)+"px; left: "+(e.pageX-12)+"px;")})),document.addEventListener("click",(e=>{cursor.classList.add("clickExpand"),cursor2.classList.add("clickExpand"),setTimeout((()=>{cursor.classList.remove("clickExpand"),cursor2.classList.remove("clickExpand")}),700)}));const NAV_SLIDE=()=>{const e=document.querySelector(".hamburger"),t=document.querySelector(".nav-links"),s=document.querySelectorAll(".nav-links li");e.addEventListener("click",(()=>{t.classList.toggle("nav-active"),s.forEach(((e,t)=>{e.style.animation?e.style.animation="":e.style.animation=`navLinkFade .5s ease forwards ${t/7+.5}s`})),e.classList.toggle("cross")}))};NAV_SLIDE();